    private void okMouseClicked(java.awt.event.MouseEvent evt) {                                
        //Obtener el indice seleccionado del combo box
        if(BoxInsano.getSelectedIndex() > 0){
            Indice.setText("Indice: " + BoxInsano.getSelectedIndex());
            Item.setText("Item: "+ BoxInsano.getSelectedItem());
        }else{
            JOptionPane.showMessageDialog(null, "Debe seleccionar un Dia");
        }

        
        
        
    }                               

    private void BoxInsanoActionPerformed(java.awt.event.ActionEvent evt) {                                          
        if(BoxInsano.getSelectedIndex() > 0){
            Indice.setText("Indice: " + BoxInsano.getSelectedIndex());
            Item.setText("Item: "+ BoxInsano.getSelectedItem());
        }else{
            JOptionPane.showMessageDialog(null, "Debe seleccionar un Dia");
        }
    }                                         

